    cat("lookupInZOS.InputMap.racfpassword: This is the KEYSTORE        value   : "+KEYSTORE,2); 
    cat("lookupInZOS.InputMap.racfpassword: This is the TDICERT_ALIAS   value   : "+TDICERT_ALIAS,2);
    cat("lookupInZOS.InputMap.racfpassword: This is the RACFCERT_ALIAS2 value   : "+RACFCERT_ALIAS2,2);
      	pwbytes = conn.getObject("racfpasswordenvelope");
	    racfpwobj = system.getRacfPasswordObject ( 
		pwbytes,
		KEYSTORE, KEYSTORE_PASSWD, 
		TDICERT_ALIAS, TDICERT_PASSWD,
		KEYSTORE, KEYSTORE_PASSWD, eval(RACFCERT_ALIAS)
	);
	cat("lookupInZOS.InputMap.racfpassword: This is the racfpwobj:",2);
	cat(racfpwobj.dumpContents(),2);
	if ( racfpwobj.getExpired() == true ) {
		work.setProperty("passwordExpired","true");
	}
    else {
          work.setProperty("passwordExpired","false");
    }
	racfpw = racfpwobj.getPasswordString();
	ret.value = racfpw;
}
else {
work.setProperty("passwordExpired","true");
}

